package com.example.core.java;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int start = 1, end = 20;
		for(int i=start;i<=end;i++) {
			if(i % 2 == 0)
				System.out.println(i+" is even");
			else
				System.out.println(i+" is odd");
		}

	}

}
